CREATE TABLE IF NOT EXISTS horses(horse_ID INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, is_deleted BOOLEAN NOT NULL, name VARCHAR(20) NOT NULL, min_speed INT NOT NULL, max_speed INT NOT NULL);
CREATE TABLE IF NOT EXISTS jockeys(jockey_ID INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, is_deleted BOOLEAN NOT NULL, name VARCHAR(20) NOT NULL, sex VARCHAR(1) CHECK (sex IN ('m', 'w')), height INT NOT NULL, skill INT DEFAULT 0);
CREATE TABLE IF NOT EXISTS races(race_ID INT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, race_NR INT NOT NULL, horse_ID INT REFERENCES horses(horse_ID), jockey_ID INT REFERENCES jockeys(jockey_ID), average_speed INT NOT NULL);